public class LoginSystem {

    public static void main(String[] args) {

        boolean isLoggedIn = false; // Let's assume the user is not logged in

        // Display login status
        if (isLoggedIn) {
            System.out.println("Welcome back, user!");
        } else {
            System.out.println("Please log in to continue.");
        }

        // Now let's simulate user logging in
        isLoggedIn = true; // The user logs in successfully

        // Display updated login status
        if (isLoggedIn) {
            System.out.println("Login successful! Welcome back, user!");
        } else {
            System.out.println("Still not logged in.");
        }
    }
}
